import React from "react";
import styled from "styled-components";

const InputScreen = styled.button`
  height: 100px;
  width: 100%;
  background-color: #4357692d;
  padding: 0 10px;
  margin-buttom: 10px;
  color: white;
  display: flex;
  align-items: center;
  justify-content: flex-end;
  font-weight: bolder;
  font-size: 40px;
`;

const Input = ({ value }) => {
  return <InputScreen>{value}</InputScreen>;
};

export default Input;
