import React, { useState } from "react";

import Child from "./child";

const Parent = () => {
  let [value, setValue] = useState(null);
  return (
    <div>
      <p>The value from child should be: </p>
      <input
        type="text"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      <Child value={value}></Child>
    </div>
  );
};

export default Parent;
